import axios from 'axios';
import { SendDeSoRequest, SendDeSoResponse } from 'deso-protocol-types';
import { Identity } from '../identity/Identity';

import { Node } from '../node/Node';
export class Wallet {
  node: Node;
  identity: Identity;
  constructor(node: Node, identity: Identity) {
    this.node = node;
    this.identity = identity;
  }

  public async sendDesoRequest(
    request: Partial<SendDeSoRequest>
  ): Promise<SendDeSoResponse> {
    const endpoint = 'send-deso';
    const apiResponse = await axios.post(
      `${this.node.getUri()}/${endpoint}`,
      request
    );

    return await this.identity
      .submitTransaction(apiResponse.TransactionHex)
      .then(() => apiResponse)
      .catch(() => {
        throw Error('something went wrong while signing');
      });
  }
}
